mapper.update:
update opencourse


        semester = #{semester},


        courseId = #{courseId},


        jobNumber = #{jobNumber},


        classTime = #{classTime}



    id = #{id}

mapper.select:
SELECT
    oc.*,
    c.name AS courseName,
    COALESCE((
        SELECT AVG(cs.totalScore)
        FROM courseselection cs
        WHERE cs.semester = oc.semester
        AND cs.courseId = oc.courseId
        AND cs.jobNumber = oc.jobNumber
    ), 0) AS averageScore
FROM opencourse oc
LEFT JOIN course c ON oc.courseId = c.courseId
WHERE oc.jobNumber = #{jobNumber}

    AND oc.semester = #{semester}


    AND oc.courseId = #{courseId}


    AND oc.classTime = #{classTime}

LIMIT #{pageNum}, #{pageSize}

SELECT oc.semester, oc.courseId, oc.jobNumber, oc.classTime, c.name as course_name, c.credit, c.creditHour, t.name as teacher_name FROM opencourse oc LEFT JOIN course c ON oc.courseId = c.courseId LEFT JOIN teacher t ON oc.jobNumber = t.jobNumber
AND oc.semester = #{semester}
AND oc.courseId LIKE CONCAT('%', #{courseId}, '%')
AND c.name LIKE CONCAT('%', #{courseName}, '%')
AND c.credit = #{credit}
AND c.creditHour = #{creditHour}
AND oc.jobNumber = #{jobNumber}
AND t.name LIKE CONCAT('%', #{teacherName}, '%')
AND oc.classTime LIKE CONCAT('%', #{classTime}, '%')
ORDER BY oc.semester DESC LIMIT #{pageNum}, #{pageSize}

SELECT
    semester, courseId, jobNumber, classTime
FROM opencourse
WHERE semester=#{semester}
  AND courseId=#{courseId}
  AND jobNumber=#{jobNumber}

SELECT AVG(cs.totalScore)
FROM courseselection cs
WHERE cs.semester = #{semester}
  AND cs.courseId = #{courseId}
  AND cs.jobNumber = #{jobNumber}
